import 'dart:async';

import 'package:flutter/material.dart';
import 'package:perfume_app/presentation/home/page/home_screen.dart';
import 'package:perfume_app/service_locator.dart';

void main() {
  WidgetsFlutterBinding.ensureInitialized();
  setupServiceLocator();
  runApp(const MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      debugShowCheckedModeBanner: false,
      title: 'Perfume App',
      theme: ThemeData(
        colorScheme: ColorScheme.fromSeed(seedColor: Colors.deepPurple),
      ),
      home: HomeScreen(),
    );
  }
}

class FadingCarousel extends StatefulWidget {
  const FadingCarousel({super.key});

  @override
  _FadingCarouselState createState() => _FadingCarouselState();
}

class _FadingCarouselState extends State<FadingCarousel> {
  final List<String> images = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
  ];

  int _currentIndex = 0;
  late Timer _timer;

  @override
  void initState() {
    super.initState();
    _startAutoFade();
  }

  void _startAutoFade() {
    _timer = Timer.periodic(Duration(milliseconds: 2500), (Timer timer) {
      setState(() {
        _currentIndex = (_currentIndex + 1) % images.length;
      });
    });
  }

  @override
  void dispose() {
    _timer.cancel();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return Stack(
      children:
          images.asMap().entries.map((entry) {
            int idx = entry.key;
            String imageUrl = entry.value;

            return AnimatedOpacity(
              duration: Duration(milliseconds: 800),
              opacity: _currentIndex == idx ? 1.0 : 0.0,
              curve: Curves.easeInOut,
              child: SizedBox(
                width: double.infinity,
                height: 250,
                child: Image.network(imageUrl, fit: BoxFit.cover),
              ),
            );
          }).toList(),
    );
  }
}
